mapper.sql:
WHERE SD.DELETEFLAG='N' AND SD.USERNO=${userno}
AND SD.DOCSTATUS IN (1,2) AND SD.DOCSTATUS=${searchExt1}
${item} LIKE CONCAT('%', #{searchKeyword},'%' )

mapper.update:
UPDATE SGN_DOC
   SET DOCTITLE=#{doctitle}, DOCCONTENTS=#{doccontents}, DOCSIGNPATH=#{docsignpath}
     , UPDATEDATE=NOW()
 WHERE DOCNO=#{docno}

UPDATE SGN_DOC
   SET DELETEFLAG='Y'
 WHERE DOCNO=#{docno}


      UPDATE SGN_SIGN SS 
       INNER JOIN SGN_DOC SD ON SD.DOCNO=SS.DOCNO AND SD.DOCSTEP=SS.SSSTEP
         SET SSRESULT=#{ssresult}, SIGNDATE=NOW(), SSCOMMENT=#{sscomment}
       WHERE SS.DOCNO=#{docno}

        UPDATE SGN_DOC
           SET DOCSTATUS = #{ssresult}
			
	        	, DOCSTEP = DOCSTEP + 1
	        
         WHERE DOCNO=#{docno}

UPDATE SGN_DOC
   SET DOCSTATUS = 0
 WHERE DOCNO=#{docno}
